import React, { useEffect } from "react";
import { Link, Route, useHistory, useParams } from "react-router-dom";
import useHttp from "../hooks/use-http";
import { getSingleQuote } from "../lib/api";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import classes from "./QuoteDetail.module.css";

const DUMMY_QUOTES = [
  {
    id: "q1",
    author: "Amey A.",
    text: "Learning React Is Fun.",
  },
  {
    id: "q2",
    author: "Diya P",
    text: "Learning React Is Boring.",
  },
  {
    id: "q3",
    text: "No it really is.",
    author: "Amey A.",
  },
];


const QuoteDetail = (props) => {
  const {
    sendRequest,
    status,
    data: loadedQuote,
    error,
  } = useHttp(getSingleQuote, true);

  const {quoteId} = params

  useEffect((quoteId) => {
    sendRequest(quoteId)
  },[sendRequest])


  const history = useHistory();
  const params = useParams();
  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);

  if (!quote) {
    return (
      <section>
        <h1>Sorry! Quote is not found</h1>
      </section>
    );
    history.push(`/quotes/quote/${params.quoteId}/comments`);
  }
  return (
    <div>
      <Link to="/quotes">
        <p>back</p>
      </Link>
      <HighlightedQuote author={quote.author} text={quote.text} />
      <Route path={`/quotes/${params.quoteId}`} exact>
        <div className="centered">
          <Link to={`/quotes/${params.quoteId}/comments`} className="btn--flat">
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path={`/quotes/${params.quoteId}/comments`}>
        <Comments />
      </Route>
    </div>
  );
};

export default QuoteDetail;
